#define TWO_PI 6.28318530718

uniform vec2 u_resolution;
uniform float u_time;

vec4 circle_gradient(vec2 uv, vec2 pos, float rad) {
	float d = length(pos - uv) - rad;
	float t = clamp(d, 0.0, 1.0);
	return vec4(vec3(length(pos - uv)/rad), 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UV coordinate of current fragment
    vec2 uv = fragCoord.xy;

    // This would be the center of your berry texture (probably vec2(0.5) for a normal full-size texture)
	vec2 center = iResolution.xy * 0.5;

    // IGNORE: Radius of berry circle
	float rad = 0.25 * iResolution.x; 
    // IGNORE: Gray background layer
	vec4 layer1 = vec4(vec3(0.7), 1.0);
	// IGNORE: Draw berry circle
    vec4 layer2 = vec4(0.7, 0.0, 0.0, circle_gradient(uv, center, rad).a);

    // Pestle radius (arbitrary constant, just play with it)
    float pestle_aoe_radius = 60.0;

    // The rotating center point of the pestle (0.7 is arbitrary constant, how far from center of berries to rotate around)
    vec2 pestle = vec2(center.x+cos(iTime)*rad*0.7, center.y-sin(iTime)*rad*0.7);

    // Calculates gradient (optional, would allow to form a dimple in the mesh by offsetting Y values based on how black it is)
    vec4 layer3 = vec4(0.7, 0.0, 0.0, 1.0) * circle_gradient(uv, pestle, pestle_aoe_radius);

	// IGNORE: Blend the layers
	fragColor = mix(mix(layer1, layer2, layer2.a), layer3, layer3.a);
}
