void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 res = iResolution;
    vec3 crimsonRed = vec3(0.73725490196, 0., 0.17647058823);

    vec2 uv = (2.0*fragCoord-iResolution.xy)/min(res.x, res.y);

    vec3 col = vec3(1.,1.,1.);

    float r = 0.4 + 2.* smoothstep(0.0,0.1,sin(atan(uv.y, uv.x)*16. - iTime*3.));

    col *= crimsonRed + smoothstep(r, r+0.01, length(uv));

    fragColor = vec4(col,1.0);
}
