void mainImage(out vec4 fragColor, in vec2 fragCoord){
    float t = iTime;
    vec3 r = iResolution;
    vec2 s = 5.*(gl_FragCoord.xy-.5*r.xy)/r.y,f=fract(s),p=floor(s);
    fragColor = vec4(step(0.,max(-length((p=.5+.5*sin(t+sin(p.xx*vec2(7,9)+p.yy*3.)*54.))-f)+.03,max(f.x,f.y)-.99))*sin(t+p*9.).xyx*cos(t+p.xxy),0);
}
