void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.xy;
  uv -= .5;
  uv.x *= iResolution.x / iResolution.y;
  uv *= 3.;
  vec2 am = vec2(atan(uv.x,uv.y),length(uv));

  float t = iTime;
  float n = 7.;

  am.x -= .3*sin(.5*(am.y+10.)*(am.y+10.)) + .3*sin(10.*am.y+10.*sin(t/5.));

  float m = 7.*min(fract(n*(am.x)/6.282),fract(1.-n*(am.x)/6.282));

  fragColor = vec4(smoothstep(.3,.2,am.y-m));
}
