void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float stripeSpacing = 10.0;
    const float stripeThickness = 0.2;
    float squircleThickness = 20.0;

    vec2 center = iResolution.xy / 2.;
    vec2 pos = fragCoord - center;
    vec2 offset = vec2(iMouse.x, iResolution.y/2.) - center;
    float mouseYRatio = iMouse.y / iResolution.y;
    float scale = 0.5 * mouseYRatio + 5.0 * (1.-mouseYRatio) ;
    pos = (pos - offset) *  1. / scale;

    const float radius = 80.;
    float shape = 4.;// + (sin(iTime) * 0.5 + 0.5) * 7.; 
    float wowsers = pow(radius, shape);

    float col = (pow(pos.x, shape) + pow(pos.y, shape)) - wowsers;

    float distFromCenter = length(pos);
    float radiusRatio = distFromCenter / radius;
    float distShape = (shape - 2.) / (pow(radiusRatio,2.0) * 0.75) + 2.;
    float distToSquircle = pow(
        pow(pos.x, distShape) + pow(pos.y, distShape),
        1. / distShape
    ) - radius;

    float stripes = mod(distToSquircle, stripeSpacing) - stripeSpacing * (1.-stripeThickness);


    float isSquircle = abs(distToSquircle) - squircleThickness;
    float isSquircleCenter = clamp(abs(distToSquircle) * 0.01, 0., 1.);
    fragColor = vec4(vec3(isSquircle), 1.0);
    fragColor.r = 1.0 - smoothstep(0.002, 0.006, isSquircleCenter);

    if (iMouse.z > 0.5) {
        fragColor.rgb = vec3(
            abs(
                pow(pos.x, 4.) + pow(pos.y, 4.) - pow(radius, 4.)
            ) - pow(30., 4.)
        );
	    //fragColor = vec4(col, stripes, 0.0, 1.0);
    }



}
