void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float count = 3.0;
    float w = 1.0;

    w = w + mod(iTime, 1.0) / count;
    w = w * abs(sin(iTime));
    float minOffset = (1.0 - w) / 2.0;
    float maxOffset = 1.0 - minOffset;

    vec4 originColor = texture(iChannel0,uv);

    vec4 overlayColor = texture(iChannel0,uv);



    if ((uv.x >= minOffset && uv.x <= maxOffset) && (uv.y >= minOffset && uv.y <= maxOffset))
    {
        uv = vec2 (uv - vec2(minOffset)) / w;
        overlayColor = texture(iChannel0,uv);
    }


    vec4 color = mix(originColor,overlayColor,0.3);

    fragColor = color;
}
