float noise(vec3 x){return 2.0*3.14159265359*(0.5*(texture(iChannel0,x).r-0.5)+0.25*(texture(iChannel0,x*2.0).r-0.5)+0.125*(texture(iChannel0,x*4.0).r-0.5)+0.06125*(texture(iChannel0,x*8.0).r-0.5));}
float swap(inout vec3[20] l, int index,vec3 tmp){l[index]=l[index+1];
                                                 return ((l[index+1]=tmp)/tmp).r;}                           
void sort(int yes,inout vec3[20] l){ 
    for(int j =0;j<19;j++){
    for(int i =0;i<19;i++){yes+=int(min(l[i].x,min(l[i].y,l[i].z))>min(l[i+1].x,min(l[i+1].y,l[i+1].z))?swap(l,i,l[i]):0.0); }}}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){vec3 list[20];
    for(int i =0;i<20;i++){list[i]=texture(iChannel1,fragCoord/iResolution.xy).rgb;
                           fragCoord+=vec2(sin(iTime+noise((vec2(iTime*0.2)+fragCoord/iResolution.xy).xyx*0.16)),cos(iTime+noise((vec2(iTime*0.2)+fragCoord/iResolution.xy).xyx*0.1))     );}
    sort(0,list);
   	fragColor  = vec4(list[1],1.0);}
