// Transfer from http://glslsandbox.com/e#66502.3

#define time iTime

#define MAX_ITERATION 120.
float mandelbrot(vec2 c)
{
	vec2 z = c;
	float count = 0.0;
	float t = time*0.2;
	for (float i = 0.0; i < MAX_ITERATION; i++)
	{
		z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) - z*cos(t)+c*sin(t);
		if (length(z) > 2.0) break;

		count += 1.0;
	}

	float re = (length(z*count/MAX_ITERATION));
	if (re <= 0.0)
		return 1.;
	return re;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
	uv *= 5.;	
    uv.x -= 2.5;
    uv.y -= 2.5;
	float mb = mandelbrot(uv);
	fragColor = vec4(vec3(mb), 1.0);
}
