// Global constants
const vec3 c = vec3(1.0, 0.0, -1.0);
float a = 1.0;

float iScale;

void rand(in vec2 x, out float n)
{
    x += 400.;
    n = fract(sin(dot(sign(x)*abs(x) ,vec2(12.9898,78.233)))*43758.5453);
}

void lfnoise(in vec2 t, out float n)
{
    vec2 i = floor(t);
    t = fract(t);
    t = smoothstep(c.yy, c.xx, t);
    vec2 v1, v2;
    rand(i, v1.x);
    rand(i+c.xy, v1.y);
    rand(i+c.yx, v2.x);
    rand(i+c.xx, v2.y);
    v1 = c.zz+2.*mix(v1, v2, t.y);
    n = mix(v1.x, v1.y, t.x);
}

void mfnoise(in vec2 x, in float d, in float b, in float e, out float n)
{
    n = 0.;
    float a = 1., nf = 0., buf;
    for(float f = d; f<b; f *= 2.)
    {
        lfnoise(f*x, buf);
        n += a*buf;
        a *= e;
        nf += 1.;
    }
    n *= (1.-e)/(1.-pow(e, nf));
}

// Stroke
void stroke(in float d0, in float s, out float d)
{
    d = abs(d0)-s;
}

// Extrusion
void zextrude(in float z, in float d2d, in float h, out float d)
{
    vec2 w = vec2(-d2d, abs(z)-0.5*h);
    d = length(max(w,0.0));
}

// Add sdfs
void add(in vec2 sda, in vec2 sdb, out vec2 sdf)
{
    sdf = sda.x<sdb.x?sda:sdb;
}

void dvoronoi(in vec2 x, out float d, out vec2 z)
{
    vec2 y = floor(x);
       float ret = 1.;
    vec2 pf=c.yy, p;
    float df=10.;

    for(int i=-1; i<=1; i+=1)
        for(int j=-1; j<=1; j+=1)
        {
            p = y + vec2(float(i), float(j));
            float pa;
            rand(p, pa);
            p += pa;

            d = length(x-p);

            if(d < df)
            {
                df = d;
                pf = p;
            }
        }
    for(int i=-1; i<=1; i+=1)
        for(int j=-1; j<=1; j+=1)
        {
            p = y + vec2(float(i), float(j));
            float pa;
            rand(p, pa);
            p += pa;

            vec2 o = p - pf;
            d = length(.5*o-dot(x-pf, o)/dot(o,o)*o);
            ret = min(ret, d);
        }

    d = ret;
    z = pf;
}

vec2 ind;
void scene(in vec3 x, out vec2 sdf)
{
    x.y += .3*iTime;

    float d, 
        d2,
        dz,
        da,
        r;
    vec2 p, 
        p2,
        y;

    // Net
    dvoronoi(6.*x.xy, d, p);
    dvoronoi(12.*x.xy, d2, p2);
    ind = p;

    x.z -= -.1+.4*p.x*p2.x/6./12.;

    // Displacement
    mfnoise(x.xy-iTime*c.yx, 2., 12., .25, dz);
    d = x.z-.1-d/12.+.1*dz-.2*d2/12.;
    //d = mix(d, length(x)-1., .01);
	sdf = vec2(d, 1.);

    // Discrete displacement
    mfnoise(p/6.-iTime*c.yx, 2., 12., .25, dz);
	add(sdf, vec2(length(x-vec3(p/6.,.14-.1*dz))-.02, 2.), sdf);

}   

void normal(in vec3 x, out vec3 n)
{
    const float dx = 1.e-2;
    vec2 s, na;

    scene(x,s);
    scene(x+dx*c.xyy, na);
    n.x = na.x;
    scene(x+dx*c.yxy, na);
    n.y = na.x;
    scene(x+dx*c.yyx, na);
    n.z = na.x;
    n = normalize(n-s.x);
}

float sm(float d)
{
    return smoothstep(1.5/iResolution.y, -1.5/iResolution.y, d);
}

void colorize(in vec2 x, out vec3 col)
{
    col = vec3(0.09,0.10,0.07);

    x.y += .3*iTime;

    float d, 
        d2,
        dz,
        da,
        r;
    vec2 p, 
        p2,
        y;

    // Net
    dvoronoi(6.*x.xy, d, p);
    dvoronoi(12.*x.xy, d2, p2);

    stroke(d, .02, d);
    col = mix(col, vec3(0.79,0.44,0.36), sm(d));

    stroke(d2, .02, d2);
    col = mix(col, vec3(0.89,0.44,0.26), sm(d2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    a = iResolution.x/iResolution.y;

    iScale = fract(iTime);

    vec2 uv = fragCoord/iResolution.yy-0.5*vec2(a, 1.0), 
        s;
    vec3 col = c.yyy, 
        o = c.yzx,
        r = c.xyy, 
        u = normalize(c.yxx),
        t = c.yyy, 
        dir,
        n,
        x;
    int N = 100,
        i;
    t = uv.x * r + uv.y * u;
    dir = normalize(t-o);

    float d = -(o.z-.2)/dir.z;

    for(i = 0; i<N; ++i)
    {
     	x = o + d * dir;
        scene(x,s);
        if(s.x < 1.e-4)break;
        if(x.z<-.1)
        {
            col = .2*c.xxx;
            i = N;
            break;
        }
        d += min(s.x,6.e-3);
    }

    if(i < N)
    {
        normal(x,n);

        if(s.y == 1.)
        {
            vec3 l = normalize(x+.5*c.yzx);
            colorize(x.xy, col);
            col = .1*col
                + 1.8*col * abs(dot(l,n))
                + 2.5 * col * pow(abs(dot(reflect(x-l,n),dir)),2.);
        }
        else if(s.y == 2.)
        {
            vec3 l = normalize(x+c.xzx);
            float r;
            rand(ind, r);
            col = mix(vec3(0.99,0.43,0.15),vec3(0.44,0.07,0.66),iScale*r);
            col = .1*col
                + .8*col * abs(dot(l,n))
                + 6.5*col * pow(abs(dot(reflect(x-l,n),dir)),2.);;
        }
    }

    col *= col;
    col = mix(col, c.yyy, clamp((d-2.-(o.z-.2)/dir.z)/4.,0.,1.));
    fragColor = vec4(clamp(col,0.,1.),1.0);
}
