#define ignore vec3(0.0549, 0.639, 0.152)
#define scale 4.0
#define offset vec2(1.0/scale,1.0/scale)
#define threshold 0.3


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cv = mod(uv, offset) * scale;

    vec4 bg = texture(iChannel0, cv);
    vec4 fg = texture(iChannel1, uv);

    float d = distance(fg.rgb, ignore);

    if(d < threshold) {
     	fragColor = bg;   
    }
    else{
        fragColor = fg;
    }
}
