#define SCALE 6.

void mainImage( out vec4 O, vec2 U )
{
    if ( U.x < .4*iResolution.x )
    { O = texelFetch(iChannel0, ivec2(U), 0).xxxx; return; }

    U = (U-.5)/SCALE;
    float v = texture(iChannel0, (.5+U)/1024.).x;     // bilinear interp

    float v00 = texelFetch(iChannel0, ivec2(U), 0).x, // bicubic interp
          v10 = texelFetch(iChannel0, ivec2(U+vec2(1,0)), 0).x,
          v01 = texelFetch(iChannel0, ivec2(U+vec2(0,1)), 0).x,
          v11 = texelFetch(iChannel0, ivec2(U+vec2(1,1)), 0).x;
    vec2 f = fract(U); 
    f = f*f*(3.-2.*f);   // comment to get bilinear interpolation
    v = mix( mix(v00,v10,f.x), mix(v01,v11,f.x), f.y); 

    float p = fwidth(v); // .75/SCALE;
    v = smoothstep(-p,p,v-.5);

    O = vec4( pow( v, 1./2.2 ) );
}
