#define W     .1*R.y* mat2(.85,-.5,.5,.97)   // grid freq & tilt
#define L(t) (  length(t.rgb) / 1. )         // luminance

void mainImage( out vec4 O, vec2 U )
{

    vec2 R = iResolution.xy,                 // grid stencil
         G = pow( .5+.5*sin( 6.3 *U/R.y* W), vec2(14.) );
    U /= R;
    O = textureLod(iChannel0, U, 4.);        // lowres color
    float m = min(O.r,min(O.g,O.b)),
          M = max(O.r,max(O.g,O.b));
    O =  (O-m)/(M-m);                        //  chrominance
 // O/= L(O);  O = min(O,1.);

    O =  mix (vec4(1), O, max(G.x,G.y) )     // color on wire only
        * L(texture(iChannel0, U));          // * luminance
}
