void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Set up and calculate values
    float n = mod(iTime, 3.0) - 1.5;  		// the "center", which moves along the x axis
    										// the 3.0 and 1.5 are semi-arbitrary
    float x = uv.x; 						// the "x" in y = bx + a
    float adj = 0.4;  						// this adjusts the intensity of the light

    float d = abs(x - n); 					// distance to the center
    float val = -2.0 * d + 1.0;				// function: y = -2x + 1
    										// measures intensity (value of white) based on distance from n, the "center"

    // Set up colors
    vec4 texColor = texture(iChannel0, uv);
    vec4 light = vec4(val - adj);

    texColor = texColor + light;

    // Output to screen
    fragColor = texColor;
}
