float hash2(vec2 p) {
    return fract(sin(p.x * 15.73 + p.y * 35.29) * 43758.29);    
}

float shash2(vec2 p) {
    return hash2(p) * 2.0 - 1.0;
}

vec2 hash22(vec2 p) {
    return fract(sin(mat2(15.23, 35.76, 75.48, 153.69) * p) * 43758.29);
}

float noise2(vec2 p) 
{
    vec2 g = floor(p);
    vec2 f = fract(p);
    f = f*f*(3.0 - 2.0*f);

    float lt = hash2(g + vec2(0.0, 1.0));
    float rt = hash2(g + vec2(1.0, 1.0));
    float lb = hash2(g + vec2(0.0, 0.0));
    float rb = hash2(g + vec2(1.0, 0.0));

    float t = mix(lt, rt, f.x);
    float b = mix(lb, rb, f.x);
    return mix(b, t, f.y);
}

float smin(float a, float b, float k) {
	float h = clamp((b - a) / k * 0.5 + 0.5, 0.0, 1.0);
	return mix(b, a, h) - k * h * ( 1.0 - h);
}


float voronoi(vec2 p) {
    vec2 g = floor(p);
    vec2 f = fract(p);
	//f = f*f*f*(6.0*f*f - 15.0*f + 10.0);
      f = f*f*f*(6.0*f*f - 15.0*f + 10.0);



    float d = 1000.0;
    for(int i = -1; i <= 1; i++) {
        for(int j = -1; j <= 1; j++) {
            vec2 b = vec2(i, j);
            d = smin(d, length(hash22(g + b) + b - f), 0.2);
        }
    }
    return d;
}



float sdPlane(vec3 p) 
{


    float m1= p.y - 0.25 * voronoi(p.xz * 2.0) + 2.0 * noise2(p.xz);
    float m2= p.y - 0.25 *  dot( sin(p.zx * 2.0) , 2.0 * sin(0.25*p.xz));

    return  m1-(2.0-m2);


}

float map(vec3 p) 
{
    float r = p.y + 0.5;
    float d = sdPlane(p) * 0.5;
    d = smin(d, r, 0.1);
    return d;
}


