#define T(ch)  texelFetch( ch, ivec2(U) % textureSize(ch,l), l).xxxx

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    O-=O;
    if (   int(U.x+2.)%int(R.x/2.) < 5     // separators
        || int(U.x)   %int(R.x/4.) < 2 
        || int(U.y)   %int(R.y/5.) < 1 )  return; 

    int l = int(5.*U.y/R.y);                // vertical: LOD bands
    float x = U.x - R.x/2.;
    if (abs(x)>R.x/4.) U /= 2.;             // extremes: zoom
    O = x<0. ? T(iChannel0) : T(iChannel1); // left: white noise  right : blue noise

    if ( iMouse.z > 0. ) O = .5 + (O-.5)*float(1<<l); // click: normalize by std-dev(avg)

    O = pow( O , vec4(1./2.2) );            // to sRGB
}
