void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(1.2) - vec3(distance(vec2(.5), uv));

    // sea
    if ((uv.y - sin(iTime + uv.x) * .15) * 5.5 -1.9  < .5 * cos(iTime) + 1. * .5) {
    	col = vec3(0., 0., .2);
    }


    // Boat

    if (distance(uv, vec2(.5, .5 + sin(iTime) * .25)) < .2) {
    	col *= vec3(.2, .2, 0.1);
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
