vec3 hsv2rgb(vec3 c) {
	vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
	vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
	return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float d(vec2 a, vec2 b) {
    return length(a - b);
}

const float scale = 2.0;
const float period = 1.0;
const float base = 1.0;
const float speed = 0.5;
const float rotate = radians(45.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = (uv * 2.0 - vec2(1.0)) * scale;
    pos = vec2(cos(rotate * iTime) * pos.x + -sin(rotate * iTime) * pos.y,
              sin(rotate * iTime) * pos.x + cos(rotate * iTime) * pos.y);
    float l =
        d(pos, vec2(-1.0, 0.0)) *
        d(pos, vec2(1.0, 0.0)) *
        d(pos, vec2(0.0, -1.0)) *
        d(pos, vec2(0.0, 1.0));
    float r = abs(mod(iTime * speed, 2.0 * period) - period) + base;
    float val = min(max(r - l, 0.0), 1.0);
    float angle = acos(normalize(pos).y);
    if (pos.x < 0.0) {
        angle = radians(360.0) - angle;
    }
    fragColor = vec4(hsv2rgb(vec3(mod(degrees(angle) / 360.0 - mod(iTime / 10.0, 1.0), 1.0), val, 1.0)), 1.0);
}
