vec3 neonPurple = vec3(99.0/255.0,36.0/255.0,177.0/255.0);
vec3 neonBlue = vec3(21.0/255.0, 47.0/255.0, 203.0/255.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 glowCenter = vec2(-0.1, -0.1);

    vec2 p = glowCenter - uv;

    vec2 glowCenter1 = vec2(1.1, 1.1);

    vec2 p1 = glowCenter1 - uv;

    float dist = 1./length(p);

	dist *=  iMouse.x/(iResolution.x*.04);

    dist = pow(dist, 0.8);

    float dist1 = 1./length(p1);

    dist1 *= 0.7;

    dist1 = pow(dist1, 0.8);

    // Output to screen
    vec4 glowColor = mix(vec4(neonBlue*dist1,1.0), vec4(neonPurple*dist,1.0), 0.7);
    fragColor = texture(iChannel0, uv.xy);
    fragColor += glowColor;
}
