float remap(float oldMin, float oldMax, float newMin, float newMax, float value){
    return newMin + (value - oldMin) * (newMax - newMin) / (oldMax - oldMin);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    uv += vec2( 0, remap( -1., 1., -.1, .1, sin(iTime * 3.) ) );

    float r = .8 * remap( -1., 1., 0.97, 1., sin(iTime * 9.) );
    float sr = .05;
    float d = abs( length( uv ) - r ) - 0.003;
    d = min(d, length(uv) - .05 * remap(-1., 1., 0.8, 1.1, cos(iTime*6.)) );

    float div = 16.;
    float TAU = 6.28318530718;
    float a = TAU / div;

    for ( float i = 0.; i < div; i++ ) {
        float rad = a * i + remap(-1.,1.,0.5,15.,sin(iTime));
        float radt = TAU - rad;
        vec2 pos = r * vec2( cos( rad ), sin( rad ) );
        vec2 post = r * vec2( cos( radt ), sin( radt ) );
        float srd = remap(-1., 1., 0.5, 1.7, sin( iTime * 5. + (i * .8 + 1.)) );
        float sd = length( uv - mix( pos, post, (sin(iTime) + 1.) * .5) ) - sr * srd;
        d = min( d, sd );
    }

    fragColor = vec4( 1. - iResolution.y/3. * d );
}
