// 2D rotate
mat2 rotate(in float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 st = (vec3(2.0 * fragCoord.xy - iResolution.xy, iResolution.y));
    st = normalize(vec3(st.xy, sqrt(max(st.z * st.z - dot(st.xy, st.xy) *2.,0.))));
    st.xy *= rotate(iTime*0.1);

    st *= 21.*abs(1.1 + sin(iTime*0.3));

    float len;
    for (int i = 0; i < 10; i++) {
    	len = length(st);
        st.x +=  sin(st.y + iTime * 0.3)*1.;
        st.y +=  cos(st.x + iTime * 1.0 + cos(len * 1.0))*1.;
    }

    vec3 color = cos(len + vec3(0.2,0.1,-0.05));
    fragColor = vec4(color, 1.0);
}
