float map(float a, float b, float c, float d, float t)
{ 	return ((t-a)/(b-a))*(d-c)+c;}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  int x = int(fragCoord),                   				
      y = int(fragCoord.y + 80. * iTime),   				
      r = (x+y)^(x-y-8000);             			
 vec3 c = fract(vec3(r*r*r)/(4.5+vec3(15,233.9,2.))*3.5);

     bool u = (r*r*r/(y+x+int(sin(iTime)*1000.+10000.)) % (9999) < 1000);
    vec3 c2 = vec3(u);
 //https://www.shadertoy.com/view/wtlcR8 --   
//bool b = abs(r*r*r/(y+x+int(-iTime*10.9))) % (9999) < 1000;
 //   ----
//vec3 c2 = vec3(vec3(b ? 1.0 : 0.0));
 fragColor= vec4(c.x, map(0.0,0.6,.35,0.,c.y),c2.z*0.35, 1.);      
}
