uint hash( uvec2 x ){
    uvec2 q = 1103515245U * ( (x>>1U) ^ (x.yx   ) );
    uint  n = 1103515245U * ( (q.x  ) ^ (q.y>>3U) );
    n = n^(n >> 16);
    return n;
	}

void mainImage( out vec4 C, in vec2 F ){
   vec2 R=iResolution.xy; ivec2 f=ivec2(F), r=ivec2(R); 
   uint h=hash(uvec2(f+iFrame));     
   uint b=uint(floatBitsToInt( texelFetch(iChannel0,f,0).x   ));  // read 32bits from float buffer
   if(f.y>r.y>>1)b&=(1u << f.x)^0xFFFFFFFFu;   // top half, zero a single bit to verify error checking     
   C=vec4( float(h!=b),0,0,1 );                // Red if error
   if(f.x>r.x>>1)C=vec4( float(b>>24u)/255. ); // right half, show top 8 bits of (partially corrupted) 32bit binary data 
   }

uint hash( uvec2 x ){
    uvec2 q = 1103515245U * ( (x>>1U) ^ (x.yx   ) );
    uint  n = 1103515245U * ( (q.x  ) ^ (q.y>>3U) );
    n = n^(n >> 16);
    return n;
	}


void mainImage( out vec4 C, in vec2 F ){
   ivec2 f=ivec2(F);
   uint h=hash(uvec2(f+iFrame));    
   C=vec4( intBitsToFloat(int(h)), 0,0,0); // store 32bits as float
   }
