#define Segment 6.0 
#define Width 0.2


float hash(vec2 uv){
    return fract(45873.15641 * sin(dot(uv, vec2(15.153,21.556))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord /min(iResolution.x,iResolution.y);

    uv *=5.0;
    vec2 i = floor(uv);
    vec2 f = fract(uv);

    float t = iTime;
    float thash = floor(t /5.0);
    t = mod(t,5.0);
    float seed = clamp(t, 0.0,.25); 

    float r = hash(i + vec2(seed + thash));
    vec2 fi = floor(f * Segment);
    vec2 ff = fract(f * Segment);
    float rnd = hash(fi + r);
    float fr = step(hash(fi),0.5);
    if(rnd < 0.25){
        fr = step(Width,ff.x);
    } else if(rnd < 0.5){
        fr = step(Width,ff.y);
    } else if(rnd < 0.75){
        fr = step(Width,1.0 -ff.x);
    } else {
        fr = step(Width,1.0 - ff.y);
    }
    fr *= step(hash(fi + r + vec2(12.45,18.13)),0.9);
    // Time varying pixel color
    vec3 c = vec3(fr);
    c *= fi.x * (fi.x - Segment + 1.0);
    c *= fi.y * (fi.y - Segment + 1.0);

    c = clamp(c,0.0,1.0);
    // Output to screen
    fragColor = vec4(c,1.0);
}
