void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 o = -3.1416*vec3(0., .5, 1.);

    float g = uv.y+iTime;
    vec3 col = .5+.5*-sin(g)*cos(g+o);

    col.g += .25;
    col = .5+(col*2.-1.);
    col.gb *= vec2(.75, .9);
    col = .125+.75*col;

    //col = (.01+uv.y*uv.y)*.01/(abs(col-uv.x)+.001); //graph

    fragColor = vec4(col, 1);
}
