#define PI 3.141592
#define mtime mod(iTime,PI)

float beat() {
 if(mtime < PI/2.) {
 float a = sin(mtime*4.);
 float b = sin(mtime*4.+PI/4.);
    return pow(a,8.)-pow(b,8.);
 }
  return 0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 uv2 = uv;
    uv.y -= abs(uv.x*.5-(length(uv2)*2. *beat()/20.  )*2.);
    float m = smoothstep(0.3000000001,0.299,length(uv));
    m -= smoothstep(0.5,0.6,abs(sin(iTime*10.+uv2.y*50.)));

    vec3 color = mix(vec3(0.,0.,0.),vec3(0.9,0.3 ,clamp(0.7+sin(uv2.y+iTime*1.0)-cos(uv2.x*10.)*.2,0.,1.)),m);
    fragColor = vec4(color,
        1.0);
}
