void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0,uv).xyz;

    // Output to screen
    fragColor = vec4(1. - col,1.0);
}

float noise(float t){
	return 0.5 + 0.5 * (sin(t * 4.) + cos(t * 2.) + sin(t / 2.)) / 3.;
}

vec2 path(float t){
	float x = (2./3. * cos(t * 25.) + 1./3. * cos(t * 40. * 1.5));
    float y = (2./3. * sin(t * 40.) - 1./3. * sin(t * 40. * 1.5));
    return vec2(x,y) * (0.75 + noise(t) * 0.75)/2. ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy)/iChannelResolution[1].xy;
    vec3 col= texture(iChannel1,uv).xyz;
    col *= 0.99;
    for(float i = 0.; i < 0.05; i += 0.00125){
        vec2 ce = path(iTime/4. - i);
        float rate = 0.5 + 0.1 * sin(iTime);
    	col = length(uv - ce * rate - 0.5) < 0.0025 ? vec3(0.9) : col;
    	fragColor = vec4(col,0.);
    }

}
