float PI = 3.14159;

void mainImage( out vec4 c, in vec2 x )
{
    vec3 r;
    x.x=x.x/iResolution.x*4.0;

    x.x=x.x;//-iTime;
    r.r=sin(x.x*PI*1.0);
    r.g=sin(x.x*PI*1.0+PI/3.0);
    r.b=sin(x.x*PI+PI*2.0/3.0);

    //r=r*r;
    r=abs(r);
    r.r=pow(r.r,0.0001);
    r.g=pow(r.g,0.0001);
    r.b=pow(r.b,0.0001);

    r=r-min(min(r.r,r.g),r.b);
    r=r/max(max(r.r,r.g),r.b);

    c = vec4(r,1.0);
}
