// Fork of  https://shadertoy.com/view/Wl2SWc

void mainImage( out vec4 O, vec2 u )
{
    if (u.y==5.5) { O.r = float(textureSize(iChannel1,0).y==1); return; } 
    if (u.y==9.5) { O.g = step(u.x , float(textureSize(iChannel1,0).y)); return; } 
    vec2 R = iResolution.xy,
         U = u /= R;
    U.y = floor(2.*U.y)/2.+.25;   // (since nearest is ignored for sound)
    O.g = texture(iChannel1,U).g; // green: right music 
    O.r = texture(iChannel1,U).r; // red:   left music
    O.b =  O.r-O.g;               // difference
    if ( U.y > .5 ) O.b += .5; else O.b = abs(O.b);
    if ( U.y > .5 ) O *= smoothstep(10./R.y,0.,abs(O-(2.*u.y-1.)) );
}
