vec2 getUVCoords(in vec2 fragCoords)
{
    vec2 standardUVs = (fragCoords - 0.5) / (iResolution.xy - 1.0);
    return standardUVs;
}

float getRandomValue(vec2 uvCoords)
{
    return fract(sin(fract(iTime) / 10.0 * dot(uvCoords, vec2(12.9898, 78.233))) * 43758.5453123);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float noiseFactor = .3;
    float able = abs(sin(iTime*.3));

    float PEAKS = 5.0;
    float BAND = 10.0 + sin(iTime*5.);

	float mx = max(iResolution.x, iResolution.y);
    vec2 uv = fragCoord / mx;
    vec2 center = iResolution.xy / mx * 0.5;
    vec2 p = uv - center;

    float ang = atan(p.y, p.x);
    float d = distance(uv, center);
    float band0 = floor(d * BAND);
    float band1 = 1.0 + band0;
    float band2 = 1.0 + band1;

    float wav1 = cos(ang * PEAKS * band1 * band1);
    float wav2 = cos(ang * PEAKS * band2 * band2);
    float wavT = (d - band0 / BAND) * BAND;
    float wav = 0.1 * mix(wav1, wav2, clamp(wavT, 0.0, 1.0));

    float r = sin(-iTime + 100.0 * d * (1.0 + wav));
    float g = -r;
    float b = cos(iTime * 3.0 + 100.0 * d);

    	r = able * (1. - noiseFactor) * r;
        g = able * (1. - noiseFactor) * g;
        b = able * (1. - noiseFactor) * b;

    uv = getUVCoords(fragCoord);
    vec4 image = vec4(r, g, b, 1.0);

    float gRV = getRandomValue(uv) * ( noiseFactor + noiseFactor * able);

    fragColor = gRV + image;
}
