vec2 N22(vec2 p){
	vec3 a = fract(p.xyx*vec3(112.35,500.124,380.672));
    a += dot(a,a+12.25);
	return fract(vec2(a.x*a.y,a.y*a.z));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    vec3 col = vec3(0);

    float m = 0.;
    float t = iTime + 100.;
    float minDist = 100.;
    float cellIndex = 0.;
    float n = 400.;
    for(float i=0.;i<n;i++){
    	vec2 n = N22(vec2(i));
        vec2 p = (sin(n*t*.1)*.5+.5) * iResolution.x / iResolution.y;

        float d = length(uv-p);
        m += smoothstep(0.02,0.01,d);
        if(d < minDist){
            minDist = d;
            cellIndex = i;
        }
    }

    vec3 randomColor = N22(vec2(cellIndex/n)).xyx;
    col =  vec3(0.2) - sin(t*0.5 * (vec3(randomColor) + vec3(0.254,0.156,0.356)))*0.5+0.5;
    fragColor = vec4(col,1.0);
}
