float magnitude(vec2 v) {
	return sqrt(v.x * v.x + v.y * v.y);
}

float flap(float t) {
    float m = mod(t, 4.0);
    if (m < 2.0) {
        return m - 1.0;
    } else {
     	return 3.0 - m;   
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    // first wing
    vec2 p1 = uv - vec2(0.5,0.5);
    vec2 p2 = uv - vec2(0.5 + flap(iTime * 2.0) / 10.0,0.9);
    float m1 = magnitude(p1 * vec2(2.0, 1.0));
    float m2 = magnitude(p2 * vec2(2.0, 1.0));
    if ((m1 + m2) < 0.5) {
		fragColor = vec4(1.0,1.0,1.0,1.0);
    } else if ((m1 + m2) < 0.51) {
     	fragColor = vec4(0.0,0.0,0.0,1.0);
    } else {
		fragColor = vec4(1.0,1.0,1.0,1.0);
    }

    // second wing
    vec2 p3 = uv - vec2(0.5,0.5);
    vec2 p4 = uv - vec2(0.5 + flap(iTime * 2.0 + 2.0) / 10.0,0.9);
    float m3 = magnitude(p3 * vec2(2.0, 1.0));
    float m4 = magnitude(p4 * vec2(2.0, 1.0));
    if ((m3 + m4) < 0.5) {
        fragColor = vec4(1.0,1.0,1.0,1.0);
    } else if ((m3 + m4) < 0.51) {
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }

    // body
    vec2 p5 = uv - vec2(0.3,0.5);
    vec2 p6 = uv - vec2(0.7,0.5);
    float m5 = magnitude(p5);
    float m6 = magnitude(p6);
    if ((m5 + m6) > 0.51) {
    } else if ((m5 + m6) > 0.5) {
		fragColor = vec4(0.0,0.0,0.0,1.0);
    } else {
        float y = mod(uv.x - sqrt(0.21),sqrt(0.21) / 3.0);
        if (y > sqrt(0.21) / 6.0) {
            y = 1.0;
        } else {
            y = 0.0;
        }
 		fragColor = vec4(y,y,0.0,1.0);
    }

    // eye
    vec2 p7 = uv - vec2(0.72,0.52);
    float m7 = magnitude(p7 * vec2(1.0, sqrt(iResolution.y / iResolution.x * 0.5)));
    if (m7 < 0.01) {
		fragColor = vec4(0.0,0.0,0.0,1.0);
    }
}
