renderpass.code:
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 pos = fragCoord.xy/iResolution.xy;

    pos.x += sin(iTime + pos.x * 3.0) * 0.1;
    pos.y += cos(iTime + pos.y * 3.0)*0.15;
    pos.y *= -1.0;
    pos = mod(pos*12.0, 1.0);

    vec3 color = vec3(1.0);

    if(pos.x > pos.y) {
    	color = vec3(0.0);
    }

    // Output to screen
    fragColor = vec4(color,1.0);

