int strength = 5;
int maxSamples = 10;

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 color = texture( iChannel0, uv);
    float iterations = 1.0;
    int sampleRate = strength > maxSamples ? strength / maxSamples : 1;
    for(int i = -strength; i < strength; i+=sampleRate)
    {
        for(int j = -strength; j < strength; j+=sampleRate)
        {
            vec2 uv2 = uv + vec2(j, i)/iResolution.xy;
            color += texture(iChannel0, uv2);
            iterations++;
        }
    }
    color = color / iterations;
    fragColor = color;
}
