vec4 DrawSlider(vec2 uv,float width,float height,float value)
{
    vec4 col = vec4(0.);
    if(abs(uv.y) < height && abs(uv.x) < width)

        if(uv.x+width < value*width*2.)
            col = vec4(mix(vec3(1,0,0),vec3(0,1.,0),value),1);
        else
        	col = vec4(.1,.1,.1,1);
    return col;
}

const float width = 0.4;
const float height = 0.02;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy) / iResolution.x;

    float v = iMouse.x / iResolution.x;

    //Remap value by slider width
    v-=0.5;
    v *= 1. / (width * 2.);
    v+= .5;
    v = clamp(v,0.,1.);


    fragColor = DrawSlider(uv,width,height,v);
}
