vec2 hash(vec2 integerCoord){
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    vec2 x = integerCoord*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)));//Copy from https://www.shadertoy.com/view/XdXGW8
}//减掉1.0去掉了一些不希望的图案，分布在了-1到1，所以后面需要*0.5+0.5来重新映射

float gradientNoise(vec2 coord){
	vec2 fCoord = fract(coord);
    vec2 integerCoord = floor(coord);

    vec2 interpolation = fCoord * fCoord * (3.0 - 2.0*fCoord);

    return mix( mix( dot( hash( integerCoord + vec2(0.0,0.0) ), fCoord - vec2(0.0,0.0) ), 
                     dot( hash( integerCoord + vec2(1.0,0.0) ), fCoord - vec2(1.0,0.0) ), interpolation.x),
                mix( dot( hash( integerCoord + vec2(0.0,1.0) ), fCoord - vec2(0.0,1.0) ), 
                     dot( hash( integerCoord + vec2(1.0,1.0) ), fCoord - vec2(1.0,1.0) ), interpolation.x), interpolation.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float count = 16.0;
    vec2 uv = fragCoord/iResolution.xy;
	float noise = gradientNoise(uv*count);
    noise = noise * 0.5 + 0.5;
	fragColor = vec4(noise,noise,noise,1.0);
}
