float rand(vec2 co){
    return fract(sin(dot(co.xy, vec2(12.9898,78.233))) * 43758.5453);
}


float remap(float v, float minI, float maxI, float minO, float maxO) {
    return mix(minO, maxO, (v-minI)/(maxI-minI));
}

float vmin(vec2 uv) { return min(uv.x, uv.y); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5)/iResolution.y*2.1;
    if(abs(uv.x)>1. || abs(uv.y)>1.) return;

	vec4 bounds = vec4(-1, -1, 1, 1);

    bool axis = true;
    float id= 0.;
    float radius = .5;
    int itrs = 14;
    for(int i = 0 ; i < itrs ; i++ ){
        axis = !axis;
        float h = rand(vec2(id))+1.;
        float p = sin(iTime*.2 + h*(iTime*.3+100.)) * radius;
        if(axis) {
        	p = remap(p, -1., 1., bounds.x, bounds.z);
            if(uv.x>=p) {
                bounds.x = p;
                id = rand(vec2(id, 1));
            }
            else {
                bounds.z = p;
                id = rand(vec2(id, 0));
            }

        }else {
            p = remap(p, -1., 1., bounds.y, bounds.w);
            if(uv.y>p) {
                bounds.y = p;
                id = rand(vec2(0, id));
            }else{
                bounds.w = p;
                id = rand(vec2(1, id));
            }
        }
    }

    float brightnessCorrection = pow(1.-radius/2.0,float(itrs)); 
    float b = 1.-length(uv-(bounds.xy+bounds.zw)/2.) / vmin(bounds.zw-bounds.xy) * 2.;


    vec4 samples = vec4(
        mix(bounds.xy, bounds.zw, .2),
        mix(bounds.xy, bounds.zw, .8));
    vec4 color = 
        texture(iChannel0, samples.xy) +
        texture(iChannel0, samples.zw) +
        texture(iChannel0, samples.xw) +
        texture(iChannel0, samples.zy);
   	fragColor = color*b;
}
