void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cuv = uv - vec2(0.3,0.5+0.02*cos(iTime/2.0));
	cuv.x = cuv.x*iResolution.x/iResolution.y;
    float r = 0.2 + 0.1*cos(atan(cuv.y, cuv.x)*10.+30.*cuv.x+ cos(iTime));
	float d = smoothstep(r , r +.01, length(cuv));
    vec3 col = mix(vec3(.9,.1,.2), vec3(1.,.5,.2), pow(uv.y,.3));
    float is3Y = smoothstep(.3,.31, uv.y);
    col = col * is3Y + (1. - is3Y) * mix(vec3(.9,.9,.1), vec3(.4,.4,.4),abs(cuv.x));
    col *= d;
    // vignette
    col *= 1. - smoothstep(0.1,0.5,pow(length(uv - vec2(.5)),2.));

    float trunk = 0.025;
    trunk += 0.005*cos(uv.y*100.);

    col *= 1. - (1. - smoothstep(trunk,trunk+.001, abs(cuv.x - 0.3*sin(cuv.y*3.))))*(1.-smoothstep(0.,0.1,cuv.y));
    // Output to screen0.3
    fragColor = vec4(col,1.0);
}
