void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec3 background_color = 0.5 + 0.5*cos(iTime+3.0+uv.xyx+vec3(0,2,4));
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    if(uv.y<0.8 && uv.y>0.2)
    {
    	if(uv.x<=0.1 && uv.x>=0.09){
    		fragColor = vec4(col,1.0);
    	}
  		else
    	{
        	fragColor = vec4(background_color,1.0);
    	}


        if(uv.x*2.0+0.32 > uv.y && uv.x*2.0+0.32 < uv.y+0.03 && uv.y>0.5)
            fragColor = vec4(col,1.0);


        if(-1.0*uv.x*2.0+0.71 > uv.y && -1.0*uv.x*2.0+0.71 < uv.y+0.03 && uv.y<0.5)
            fragColor = vec4(col,1.0);


        if(uv.x<=0.3 && uv.x>=0.29)
            fragColor = vec4(col,1.0);

        if(3.0*uv.x - 0.67 > uv.y && 3.0*uv.x - 0.67 < uv.y+0.03)
        	fragColor = vec4(col,1.0);

        if(uv.x<=0.5 && uv.x>=0.49)
        	fragColor = vec4(col,1.0);

        if(uv.x<=0.6 && uv.x>=0.59)
        	fragColor = vec4(col,1.0);

        if(pow(uv.x-0.6,2.0)+pow(uv.y-0.659,2.0)<0.02 && pow(uv.x-0.6,2.0)+pow(uv.y-0.659,2.0)>0.017 && uv.x>0.6)
            fragColor = vec4(col,1.0);

        if(5.0*uv.x - 3.45 > uv.y && 5.0*uv.x - 3.45 < uv.y+0.05)
        	fragColor = vec4(col,1.0);

        if(-1.0*5.0*uv.x +5.1 > uv.y && -1.0*5.0*uv.x +5.1 < uv.y+0.05)
        	fragColor = vec4(col,1.0);

        if(uv.y<0.51 && uv.y>0.49 && uv.x>0.791 && uv.x<0.919)
        	fragColor = vec4(col,1.0);

    }
    else
    {
        fragColor = vec4(background_color,1.0);
    }




}
