#define EPSILON 0.01
#define PI 3.1415

mat2 rot(float a){
    return mat2(
        +cos(a), -sin(a), 
        +sin(a), +cos(a)
    );
}

float SDFCircunsferencia(vec2 p, float r){
	return length(p) - r;
}

float SDFRectangulo(vec2 p, vec2 s){
    vec2 a = abs(p) - s;
    return length(max(a, 0.0)) + min(max(a.x, a.y), 0.0);
}

// Escena
float escena_sdf(vec2 p){

    // Escalado de la escena a la mitad
    float k = 0.5;
    p = p / k;

    // Rotamos el rectángulo 45 grados
    vec2 pr = p * rot(PI / 180. * 45.0);

    // Trasladamos el rectangulo hacia 0.1 izquierda y 0.2 a la derecha.
    vec2 pt = p - vec2(0.4, 0.15);

    // Multiplicamos por la distancia
    return max(
        SDFRectangulo(pr, vec2(0.3)),
        -SDFCircunsferencia(pt, 0.3)
    ) * k;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Preservamos el ratio aspecto y re-centramos
    vec2 uv = (fragCoord-iResolution.xy * 0.5)/min(iResolution.x, iResolution.y);
    // Para cada punto p (uv), aplicamos la funcion de distancia con signo
    float d = escena_sdf(uv);

    vec3 col;
    if(abs(d) < EPSILON){
        col = vec3(1.0);
    }else{
        if(d < 0.0){
    		col = vec3(1.0, 0., 0.);
    	}else{
    		col = vec3(0., 0., 1.0);
    	}
        // Numero de repeticiones
        float k = 30.0;
        // Tomamos la distancia en valor absoluto y nos quedamos con la parte fraccional
        // Este valor esta [0,1].
        col = col * (0.5 + 0.5 * (fract(abs(d) * k)));
    }

    // Devolvemos el color asignado
    fragColor = vec4(col,1.0);
}
