const float scale = 100.0;
const vec2 ref = vec2(1,0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
	vec3 col = vec3(0);
    col = 0.4*vec3(fract(uv.x/scale), fract(uv.y/scale), 0.0);
    float dist = distance(uv, iResolution.xy*0.5);
    float dist2 = distance(uv, iMouse.xy);
    col += 0.4* vec3(0.02*sin(sin(dist))*dist);
    col += 0.4* vec3(0.02*sin(sin(dist2))*dist2);

    fragColor = vec4(col,1.0);
}
