float Circle(vec2 uv,vec2 p, float r, float blur){
    float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);

    return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Center Position -0.5 <> .5
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;



    float B = .01;

    float c = Circle(uv, vec2(0, 0.), .2, B);
    c += Circle(-uv, vec2(sin(iTime)*.38, -.09*sin(iTime*3.)), .15, B);
    c += Circle(-uv, vec2(sin(iTime)*.65, -.18*sin(iTime*3.)), .1, B);
    c += Circle(-uv, vec2(sin(iTime)*.8, -.3*sin(iTime*3.)), .05, B);

    float c1 = Circle(uv, vec2(0, 0.), .2, B);
    c1 += Circle(uv, vec2(sin(iTime)*.38, sin(iTime*3.)*.09), .15, B);
    c1 += Circle(uv, vec2(sin(iTime)*.65, sin(iTime*3.)*.18), .1, B);
    c1 += Circle(uv, vec2(sin(iTime)*.8, sin(iTime*3.)*.3), .05, B);

    float c2 = Circle(uv, vec2(0, 0.), .2, B);
    c2 += Circle(-uv, vec2(sin(iTime)*.38, sin(iTime*3.)*.09), .15, B);
    c2 += Circle(-uv, vec2(sin(iTime)*.65, sin(iTime*3.)*.18), .1, B);
    c2 += Circle(-uv, vec2(sin(iTime)*.8, sin(iTime*3.)*.3), .05, B);

    float c3 = Circle(-uv, vec2(0, 0.), .2, B);
    c3 += Circle(-uv, vec2(-.38*sin(iTime), sin(iTime*3.)*.09), .15, B);
    c3 += Circle(-uv, vec2(-.65*sin(iTime), sin(iTime*3.)*.18), .1, B);
    c3 += Circle(-uv, vec2(-.8*sin(iTime), sin(iTime*3.)*.3), .05, B);





    vec4 red = vec4(1., 0., 0., 1.);
    vec4 blue = vec4(0., 0., 1., 1.);



    // Output to screen
    fragColor = vec4(vec3((c+c1)+(c2+c3)),1.0)*vec4(red);
}
