float circle(vec2 uv){
    return smoothstep(0.1,0.09,length(uv))-smoothstep(0.09,0.08,length(uv));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    mediump int a = 1;
    vec2 uv = fragCoord/iResolution.xy;
	vec2 p = uv;
    vec2 g = uv;
    p  -= vec2(0.1,0.5);
    g -= vec2(0.9,0.5);
    uv-=vec2(0.5);
    uv.y = uv.y-(sqrt(abs(uv.x/1.4)));
    p.y = p.y-(sqrt(abs(p.x/1.4)));
    g.y = g.y-(sqrt(abs(g.x/1.4)));
   	float k = circle(uv);
    float m = circle(p);
    float r = circle(g);
    // Output to screen
    fragColor = vec4(k)*vec4(sin(iTime*2.),0.,0.,1.)+vec4(m)*vec4(1.,0.4,0.7,1.)*(cos(iTime*2.))+vec4(r)*vec4(0.,1.,0.0,1.)*(cos(iTime*2.));
}
