/* Draws lines*/
float plot(vec2 st, float pct){
  return  smoothstep( pct-0.02, pct, st.y) -
          smoothstep( pct, pct+0.02, st.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /* Aspect ratio */
    vec2 uv = 2.0*(fragCoord.xy / iResolution.xy - vec2(0.5))*vec2(iResolution.x/iResolution.y,1);
    /*  Texture color */
   	vec4 pebbles = texture(iChannel0,vec2(uv.x*iTime,uv.y*iTime));

    /* Line 1 = Static */
    float line1 = sin(uv.x);
    /* Line 2 = Moving */
    float line2 = cos(uv.x*iTime);
    vec3 colorline1 = vec3(line1);
    vec3 colorline2 = vec3(line2);

    float plotline1 = plot(uv,line1);
    float plotline2 = plot(uv,line2);
	colorline1 = (pebbles.x-plotline1*iTime)*colorline1+plotline1*vec3(0.0,1.0,0.0);
    colorline1 += (1.-plotline2*iTime)*colorline2+plotline2*vec3(0.0,1.0,0.0);

    /* Draws and colors shader */
    fragColor = vec4(vec3(.3*colorline1.x,.6*colorline1.y,colorline1.z),1.);
}
