#define A 1.
#define W 1000.
#define t iTime
#define omega 50.

float lorentzian(vec2 x)
{
    return A/(1. + W*dot(x,x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy/2.)/iResolution.y;

    vec2 x0 = 0.1*vec2(sin(omega*t), cos(omega*t));

    // Output to screen
    fragColor = vec4(vec3(lorentzian(uv - x0)), 1.0);
}
