vec2 BarrelDistortion(vec2 uv, float mouse, float k){
    vec2 center = uv - 0.5;

    float radius = length(center);

    float radiusp2 = (k + pow(radius, 2.0)) * radius;

    float f = mix(radius, radiusp2, mouse);

   	float a = atan(center.y, center.x);

    return vec2(cos(a), sin(a)) * f + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float mouse = iMouse.x/iResolution.x;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float k = 0.5;
    float offset = 0.025 * mouse;

    float r = texture(iChannel0, BarrelDistortion(uv, mouse, k + offset)).r;
    float g = texture(iChannel0, BarrelDistortion(uv, mouse, k)).g;
    float b = texture(iChannel0, BarrelDistortion(uv, mouse, k - offset)).b;

    // Output to screen
    fragColor = vec4(r, g, b, 1.0);
}
