const float horizontalCount = 50.;
const float timeScale = .2;

float random(in vec2 uv) {
	return fract(sin(dot(uv, vec2(12.9898,78.233)))* 43758.5453123);
}

float random(in float val) {
 	return fract(sin(val)*1000000.0);   
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= iResolution.y / iResolution.x;
    uv.y *= horizontalCount;
    float dir = (step(.5, random(floor(uv.y))) * 2. - 1.);
	uv.x += (dir *  iTime * ( random(floor(uv.y)) + sin(iTime * .0005) ) ) + ( mix(.0, 1., (iMouse.x / iResolution.x)) );

    vec2 iPos = floor(uv);
	vec2 fPos = fract(uv);

    float randVal = random(iPos);
    vec3 lightSalmon = vec3(255./255., 169./255., 135./255.);
    vec3 jetgrey = vec3(68./255., 65./255., 64./255.);

   	fragColor = vec4(mix(lightSalmon, jetgrey, randVal), 1.);
}
