// onetweet version by Fabrice:
/*
#define mainImage(O,u)  vec2 R = iResolution.xy, U = u-.5*R;          \
O =  .5 - .5*cos( 3.*( atan(U.x,U.y) + log2(length(U)/R.y) -2.*iTime  \
                       + vec4(-.5, 0, .5,0) ) )
*/

#define t iTime

vec3 pal(float g) {
    float f = 6.; //factor
    //f *= cos(t); //uncomment to animate colors
    vec3 o = vec3(.5, .75, 1); //offset
    o -= (o.r+o.g+o.b)/3.; //center (subtract average)
    return 0.5 + 0.5*-cos( f*(g/f+o) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 pv = vec2( atan(uv.x, uv.y), length(uv) );
    pv.y = log2(pv.y);
    pv -= t;
    pv *= 3.;

    vec3 col = pal(pv.x+pv.y);

    fragColor = vec4(col, 1);
}
