vec3 gradient(vec3 col1, vec3 col2, vec3 col3, float t1, float t2) {
	return mix(mix(col1, col2, t1), col3, t2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec3 col = gradient(vec3(1, 0, 0), vec3(0, 0, 0), vec3(1, 1, 0), uv.y+0.25, uv.y+1.);

    float d = length(uv+vec2(0, cos(iTime)));
    d = smoothstep(d, 0., 0.1);
    col -= d;

    fragColor = vec4(col,1.0);
}
