#define MAX_STEPS 16
#define EPS 1e-4

float cube(vec3 p,vec3 s){
	vec3 q=abs(p);
	vec3 m=max(s-q,0.0);
	return length(max(q-s,0.0))-min(min(m.x,m.y),m.z);
}
float sphere(vec2 p){
	return length(p)-0.1;
}

vec2 pmod(vec2 p,float n){
	float np=3.141592*2.0/n;
	float r=atan(p.x,p.y)-0.5*np;
	r=mod(r,np)-0.5*np;
	return length(p)*vec2(cos(r),sin(r));
}

float sd(vec3 p){
	p.xy=pmod(p.xy,6.0);
	p=mod(p,0.8)-0.4;
	float c1=cube(p,vec3(0.3));
	float c2=cube(p,vec3(0.1,0.4,0.1));
	float s1=sphere(vec2(0.2));
	return min(c2,s1); //OR
	//return max(c2,s1); //AND
	//return max(-c2,s1);
	//return max(c2,-s1);
}

vec3 genNormal(vec3 p){
	return normalize(vec3(
		sd(p+vec3(EPS,0.0,0.0))-sd(p+vec3(-EPS,0.0,0.0)),
		sd(p+vec3(0.0,EPS,0.0))-sd(p+vec3(0.0,-EPS,0.0)),
		sd(p+vec3(0.0,0.0,EPS))-sd(p+vec3(0.0,0.0,-EPS))
		));
}


void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 p=(2.0*fragCoord.xy-iResolution.xy)/min(iResolution.x,iResolution.y);

	float radius=0.1;
	float phi=iTime*0.5;

	//vec3 ro=vec3(0.0,0.0,iTime*0.3);
	vec3 ro=vec3(cos(phi)*radius,-sin(phi)*radius,sin(phi)*radius);
	vec3 target=vec3(0.0);

	vec3 cdir=normalize(target-ro);
	vec3 side=cross(cdir,vec3(0.0,1.0,0.0));
	vec3 up=cross(side,cdir);
	float fov=0.6;

	vec3 rd=normalize(p.x*side+p.y*up+cdir*fov);

	//vec3 rd=normalize(vec3(p,1.0));

	float d,t=0.0;
	vec3 n,rp;

	for(int i=0;i<MAX_STEPS;i++){
		rp=ro+rd*t;
		d=sd(rp);
		if(d<EPS)break;
		n=genNormal(rp);
		t+=d;
	}


	fragColor=vec4(n,1.0);

}
