// ------ Main ------ //
// Read up at http://www.fluxury.com/a5x/hopfrac1.htm and https://dzone.com/articles/hopalong-fractals
//
// Parameters are in Common
// Calculations are in Buffers. All of them are duplicates of one another.
//
// This tab is for visual flair.
// Disable bloom to see true representation.
// Pallete parameters change the colours of things.




#define bloomEnabled
#define bloomStrength 0.7
#define bloomBrightness 5.
#define bloomDiameter 0.004
#define bloomTolerance 9. 
#define bloomTaps 10.
#define bloomColourOffset vec3(1.,1.,1.)


#define palleteHue 1.6
#define palleteVariation 3.3
#define palleteParameterA 3.8
#define palleteParameterB vec3(0.19,.28,2.3)

#define brightness 0.03
#define backgroundColor vec3(0.00,0.0,0.00)


// https://iquilezles.org/articles/palettes
#define pallete(x,t) 0.5 + 0.5*sin(palleteParameterB *x + 2.*t)        


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float density = texture(iChannel0, fragCoord/iResolution.xy).z ;
    //density /= 0.07*iterationsPerFrame*bufferCnt;
    vec3 col = pallete(density*palleteVariation + palleteHue,palleteParameterA);

    col *= density * brightness ;

	#ifdef bloomEnabled
    vec3 bloom = vec3(0);
    for (float i = 0.; i < bloomTaps; i ++) {
        float idx = (i/bloomTaps) * tau;
        vec2 bloomOffset = vec2(cos(idx), sin(idx));
		float tapDensity = texture(iChannel0 , uv + bloomDiameter*bloomOffset).z;    	
        vec3 tapColour = pallete(tapDensity*palleteVariation + palleteHue,palleteParameterA);
        bloom += tapColour * tapDensity ;
    }
    bloom = bloom/bloomTaps;
    bloom *= bloomStrength;
    col += mix(backgroundColor, bloom, vec3(  pow(length(bloom) * 1. ,bloomTolerance))    )*bloomBrightness*bloomColourOffset;
    #endif

    col = pow(col, vec3(0.44)); // linear to sRGB conversion

    fragColor = vec4(col.xyz,0.);
}

// ------ Calculation Buffer ------ //
// col.xy is x and y
// col.z is density passed onto buffer B

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 col = texelFetch(iChannel0, ivec2(fragCoord), 0);
    col = texture(iChannel0, fragCoord/iResolution.xy);
    fragCoord -= iResolution.xy*0.5;
    fragCoord  *= 0.2 * (1. - zoom);

    float x = col.x;
    float y = col.y;

    float prevX = x;
    float prevY = y;

    if (iFrame == 2 ){
    	prevX = initialX;
        x = initialX;
    	prevY = initialY;
        y = initialY;
    }    

    int iterCurr = iFrame;

    for (float i = 0.; i < iterationsPerFrame; i++) {
        x = prevY - sign(prevX) * sqrt(abs((b * prevX) - c ));
        y = a - prevX;

    	prevX = x;
   		prevY = y;

    	col.z += smoothstep(dotSize,dotSize*dotSmoothing,distance(vec2(x,y), fragCoord));
    }

    col.x = x;
    col.y = y;

    if (iFrame < 2){
        col = vec4(0);

    }
    fragColor = col;
}

